import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-face-snap',
  templateUrl: './face-snap.component.html',
  styleUrls: ['./face-snap.component.scss']
})
export class FaceSnapComponent implements OnInit{
  title!: string;
  description!: string;
  createdDate!: Date;
  snaps!: number;
  imageUrl!: string;
  buttonText!: string;

  ngOnInit() {
    this.title = 'Porco Rosso';
    this.description = 'Le meilleur aviateur de tous les temps';
    this.createdDate = new Date();
    this.snaps = 95;
    this.imageUrl = "https://cdn.vox-cdn.com/thumbor/Ewo6PKQSzJshpFhv5U_kwrCkXb0=/1400x1050/filters:format(jpeg)/cdn.vox-cdn.com/uploads/chorus_asset/file/20009856/Porco_Rosso_963064296_large.jpg";
    this.buttonText = "Like"
  }

  onLike(){
    this.snaps++;
  }
  onRemoveLike(){
    this.snaps--;
  }
}

<div class="face-snap-card">
  <!-- attribute binding-->
  <img [src]="imageUrl" [alt]="title" />

  <!-- interpolation binding-->
  <h2>{{ title }}</h2>
  <p>{{ description }}</p>
  <p>Mise en ligne le {{ createdDate }}</p>
  <p>
    <button (click)="onAddLike()">{{ buttonText }}</button>
    <button (click)="onRemoveLike()">Nope</button>
    üëç {{ snaps }}
  </p>
</div>
