import { Component, OnInit, Input } from '@angular/core';
import { FaceSnap } from './models/face-snap.model';

@Component({
  selector: 'app-face-snap',
  templateUrl: './face-snap.component.html',
  styleUrls: ['./face-snap.component.scss']
})
export class FaceSnapComponent implements OnInit{

  @Input() faceSnap!: FaceSnap;
  buttonText!: string;

  ngOnInit() {
    this.buttonText = "Like üëç"
  }

  onLike(){
    if (this.buttonText === "Like üëç") {
      this.faceSnap.likes++;
      this.buttonText = "Nope üíî";
    } else {
      this.faceSnap.likes--;
      this.buttonText = "Like üëç";
    }
  }
}

<div
  data-augmented-ui="
tr-clip-x bl-clip-y inlay
"
  class="styleme"
  class="face-snap-card selected"
>
  <span class="type">{{ faceSnap.type }}</span>
  <!-- attribute binding-->
  <img [src]="faceSnap.imageUrl" [alt]="faceSnap.title" />

  <!-- interpolation binding-->
  <h2>{{ faceSnap.title }}</h2>
  <p>{{ faceSnap.description }}</p>
  <div class="footer">
    <p>
      <button (click)="onLike()">{{ buttonText }}</button>
      {{ faceSnap.likes }}
    </p>
    <p class="date">Mise en ligne le {{ faceSnap.createdDate }}</p>
  </div>
  <p class="location" *ngIf="faceSnap.location">üì° {{ faceSnap.location }}</p>
</div>
