import { Component, OnInit } from '@angular/core';
import { FaceSnap } from './face-snap/models/face-snap.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  mySnap!: FaceSnap;
  mySnap2!: FaceSnap;

  ngOnInit() {
    this.mySnap = new FaceSnap(
      'Mimosa Pudica',
      "Cette espèce dite « sensitive » est souvent utilisée en laboratoire comme espèce modèle pour l'étude des mécanismes de perception chez les plantes",
      new Date(),
      3,
      "https://upload.wikimedia.org/wikipedia/commons/thumb/4/45/Mimosa_pudica_Feuille.jpg/1920px-Mimosa_pudica_Feuille.jpg"

    );

    this.mySnap2 = new FaceSnap(
      'Codariocalyx motorius',
      "Cette plante est originaire du Bengale où l'on dit qu'elle « fait danser ses feuilles comme des serpents ».",
      new Date(),
      4,
      "https://upload.wikimedia.org/wikipedia/commons/a/a4/%E0%B4%A4%E0%B5%8A%E0%B4%B4%E0%B5%81%E0%B4%95%E0%B4%A3%E0%B5%8D%E0%B4%A3%E0%B4%BF_1.JPG"

    );

  }

}
<main>
  <app-face-snap [faceSnap]="mySnap"></app-face-snap>
  <app-face-snap [faceSnap]="mySnap2"></app-face-snap>
</main>
